// Convert current UTC time to IST (+5:30)
const now = new Date();

// Get IST offset in milliseconds (5.5 hours = 19800 seconds = 19800000 ms)
const istOffset = 5.5 * 60 * 60 * 1000;

// Create new Date adjusted to IST
const istTime = new Date(now.getTime() + istOffset);

return [
  {
    json: {
      year: istTime.getFullYear(),
      month: istTime.getMonth() + 1, // Month is 0-based
      date: istTime.getDate(),
      hours: istTime.getHours(),
      minutes: istTime.getMinutes(),
      seconds: istTime.getSeconds(),
    }
  }
];

const formatted = {};

for (const item of items) {
  const name = item.json.name;
  const start = new Date(item.json.starts_at);
  const end = new Date(item.json.ends_at);

  // Format time to hh:mm AM/PM
  const formatTime = (date) => {
    let hrs = date.getHours();
    const mins = date.getMinutes().toString().padStart(2, '0');
    const ampm = hrs >= 12 ? 'PM' : 'AM';
    hrs = hrs % 12 || 12;
    return `${hrs}:${mins} ${ampm}`;
  };

  if (!formatted[name]) {
    formatted[name] = [];
  }

  formatted[name].push(`${formatTime(start)} - ${formatTime(end)}`);
}

// Now build separate entries
const output = [];

for (const [name, slots] of Object.entries(formatted)) {
  slots.forEach((slot, index) => {
    output.push({
      json: {
        slot: `${name} ${index + 1}`,
        time: slot
      }
    });
  });
}

return output;

const raw = items[0].json.output;
const parsed = JSON.parse(raw);

const desired = Object.values(parsed).filter(entry => 
  ["Amrit", "Shubh", "Labh"].includes(entry.name)
);

return desired.map(d => ({
  json: d
}));

